class Fugue():
    def __init__(self, Exposicion, Episodio, Desarrollo, Re_Exposicion, Coda):
        self.Exposicion = Exposicion
        self.Episodio = Episodio
        self.Desarrollo = Desarrollo
        self.Re_Exposicion = Re_Exposicion
        self.Coda = Coda

    def __call__(self):
        print(f"Exposición: {self.Exposicion}, {self.Episodio}, {self.Desarrollo} {self.Re_Exposicion}")
        print(f"Episodio: {self.Coda}")
        print(f"Desarrollo: {self.Exposicion}, {self.Episodio}")
        print(f"Episodio: {self.Coda}")
        print(f"Reexposición: {self.Exposicion}, {self.Episodio}")
        print(f"Coda: {self.Coda}")
        
Requiem = Fugue("Tema", "ContraTema", "Entrada", "Complementaria", "EC")
Requiem()