Temperatura = float(input("Ingrese temperatura: "))
escala = input("Es Fahrenheit (F) o es Celsius (C): ").lower()

if escala == "f":
  celsius = (Temperatura - 32) * 5/9
  print(celsius)
elif escala == "c":
  Fahrenheit = Temperatura * 5/9 + 32
  print(Fahrenheit)
else:
  print("Escala Incorrecta")
