Requiem = {
    "Introitus" : ["Requiem", "aeternam"],
    "Kyrie" : "Eleison",
    "Sequentia" : {
        "Dies" : "Irae",
        "Tuba" : "Mirum",
        "Rex" : ["Tremendae", "Majestatis"],
        "Recordare" : ["Jesu", "pie"],
        "Confutatis" : "Maledictus",
        "Lacrimosa" : ["Dies", "illa"]
    },
    "Offertorium" : {
        "Domine" : ["Jesu", "Christe"],
        "Versus" : ["Hostias", "preces"],
    },
    "Sanctus" : {
        "Sanctus" : "Dominus Deus Sabaoth",
        "Benedictus" : "",
    },
    "Agnus" : "Dei",
    "Communio" : ["Lux", "aeterna"]
}

def print_requiem(requiem):
    for section, content in requiem.items():
        if isinstance(content, dict):
            print(f"{section}:")
            for sub_section, sub_content in content.items():
                if isinstance(sub_content, list):
                    print(f"  {sub_section}: {', '.join(sub_content)}")
                else:
                    print(f"  {sub_section}: {sub_content}")
        elif isinstance(content, list):
            print(f"{section}: {', '.join(content)}")
        else:
            print(f"{section}: {content}")

print_requiem(Requiem)